SELECT e_FilterER.*
FROM f_SplitUMCs
INNER JOIN e_FilterER ON (f_SplitUMCs.Mass_Tag_ID = e_FilterER.Mass_Tag_ID) AND (f_SplitUMCs.MaxOfClass_Abundance = e_FilterER.Class_Abundance) AND (f_SplitUMCs.MD_ID = e_FilterER.MD_ID) ;
